<?include('../../../wp-config.php');include('../../../wp-admin/includes/admin.php'); $title = "E-mail results";include('./header.php');$time = time();$insert = "INSERT INTO ".$wpdb->prefix . "wpmailer_mails (subject, body, date, sent) VALUES ('".$_POST['subject']."','".$_POST['body']."','".$time."','1')" ;$results = $wpdb->query( $insert ) or die(mysql_error());$errors = 0;$success = 0;$subscribers = 0;	if ($_POST['cat'] == "custom") {	$pickcat = ""; 	} else {	$pickcat = "AND cats LIKE '%".$_POST['cat']."%'";	}	$users = $wpdb->get_results('SELECT * FROM '.$wpdb->prefix . 'wpmailer_users WHERE verified = 1 '.$pickcat) or die(mysql_error());		foreach ($users as $user) {	$id = $user->id;	$pass = $user->pass;	$email = $user->email;	$headers = "From: " . get_option('fromfield') . "\r\n";//	if ($user->version == "html") {	$headers .= 'MIME-Version: 1.0' . "\r\n";	$headers .= 'Content-type: text/html; charset=UTF-8' . "\r\n";//	}	$subject = stripslashes($_POST['subject']);	$body = stripslashes($_POST['body']);//	if ($user->version == "text") {	//	$body .= "\n\n-----------\n";//	$body .= get_option('emailfooter');//	$body .= "\n\nYou're receiving this e-mail because you're signed up to receive e-mails from ".get_bloginfo('name').". You can manage your account at ".get_bloginfo('wpurl')."/wp-content/plugins/wp-mailer/action.php?action=manage&id=".$id."&pass=".$pass." or unsubscribe at ".get_bloginfo('wpurl')."/wp-content/plugins/wp-mailer/action.php?action=unsubscribe&id=".$id."&pass=".$pass.".";//	}//	if ($user->version == "html") {	$body .= "<br><br>-----------<br>";	$body .= str_replace("\n", "<br>", get_option('emailfooter'));	$body .= "<br><br>You're receiving this e-mail because you're signed up to receive e-mails from ".get_bloginfo('name').". You can manage your account at ".get_bloginfo('wpurl')."/wp-content/plugins/wp-mailer/action.php?action=manage&id=".$id."&pass=".$pass." or unsubscribe at ".get_bloginfo('wpurl')."/wp-content/plugins/wp-mailer/action.php?action=unsubscribe&id=".$id."&pass=".$pass.".";	$subscribers = $subscribers + 1;//	}	if (mail($email, $subject, $body, $headers)) {	echo "success: ".$email."<br>";	$success = $success + 1;	} else {	echo "<font color=\"#ff0000\">FAILED: ".$email."</font><br>";	$errors = $errors + 1;	}	}echo "<h3>A total of ".$subscribers." subscribed to this e-mail. There were ".$success." successful sends and ".$errors." errors.</h3>";include('./footer.php');?>